import React, { useState } from "react";
import { Form, Input, Button, Checkbox, notification, Space } from "antd";
import { UserOutlined, LockOutlined, MailOutlined } from "@ant-design/icons";
import { loginApi } from "../../api/user";
import {
  emailValidation,
  minLengthValidation,
  maxLengthValidation,
} from "../../utils/formValidation";

import "./RegisterForm.scss";

export default function RegisterForm() {
  const [input, setInput] = useState({
    name: "",
    lastName: "",
    email: "",
    password: "",
    repeatPassword: "",
    privatePolicy: false,
  });

  const [formValid, setFormValid] = useState({
    email: false,
    password: false,
    repeatPassword: false,
    privatePolicy: false,
  });

  const changeForm = (e) => {
    if (e.target.name === "privatePolicy") {
      setInput({
        ...input,
        [e.target.name]: e.target.checked,
      });
    } else {
      setInput({
        ...input,
        [e.target.name]: e.target.value,
      });
    }
  };

  const inputValidation = (e) => {
    const { type, name } = e.target;
    if (type === "email") {
      setFormValid({
        ...formValid,
        [name]: emailValidation(e.target),
      });
    }

    if (type === "password") {
      setFormValid({
        ...formValid,
        [name]:
          minLengthValidation(e.target, 6) && maxLengthValidation(e.target, 20),
      });

      if (name == false) {
        notification["error"]({
          message: "Todos los campos son obligatorios",
        });
      }
    }

    if (type === "checkbox") {
      setFormValid({
        ...formValid,
        [name]: e.target.checked,
      });
    }
  };

  const register = () => {
    console.log(formValid);
    const {
      name,
      lastName,
      email,
      password,
      repeatPassword,
      privatePolicy,
    } = input;
    const nameVal = name;
    const lastNameVal = lastName;
    const emailVal = email;
    const passwordVal = password;
    const repeatPasswordVal = repeatPassword;
    const privatePolicyVal = privatePolicy;
    if (
      !nameVal ||
      lastNameVal ||
      !emailVal ||
      !passwordVal ||
      !repeatPasswordVal ||
      !privatePolicyVal
    ) {
      notification["error"]({
        message: "Todos los campos son obligatorios",
      });
    } else {
      if (passwordVal != repeatPasswordVal) {
        notification["error"]({
          message: "contrase√±as incorrectas",
        });
      } else {
        notification["success"]({
          message: "Cuenta creada con exito",
        });

        const result = loginApi(input);
      }
    }
  };
  return (
    <Form className="register-form" onFinish={register} onChange={changeForm}>
      <Form.Item>
        <Input
          type="text"
          name="name"
          placeholder="Nombre"
          className="register-form-input"
          prefix={<UserOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          value={input.name}
        />
      </Form.Item>

      <Form.Item>
        <Input
          type="text"
          name="lastName"
          placeholder="Apellido"
          className="register-form-input"
          prefix={<UserOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          value={input.lastName}
        />
      </Form.Item>

      <Form.Item>
        <Input
          type="email"
          name="email"
          placeholder="Email"
          className="register-form-input"
          prefix={<MailOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          onChange={inputValidation}
          value={input.email}
        />
      </Form.Item>
      <Form.Item>
        <Input
          type="password"
          name="password"
          placeholder="Contrase√±a"
          className="register-form-input"
          prefix={<LockOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          onChange={inputValidation}
          value={input.password}
        />
      </Form.Item>
      <Form.Item>
        <Input
          type="password"
          name="repeatPassword"
          placeholder="Repita su contrase√±a"
          className="register-form-input"
          prefix={<LockOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          onChange={inputValidation}
          value={input.repeatPassword}
        />
      </Form.Item>
      <Form.Item>
        <Checkbox
          name="privatePolicy"
          checked={input.privatePolicy}
          onChange={inputValidation}
        >
          He leido y acepto la politica de privacidad.
        </Checkbox>
      </Form.Item>
      <Form.Item>
        <Space direction="horizontal" size={20}>
          <Button htmlType="submit" className="register-form-button">
            Crear cuenta
          </Button>
        </Space>
      </Form.Item>
    </Form>
  );
}
