import { basePath, apiVersion } from "./config";

export function loginApi(data) {
  const url = `${basePath}/${apiVersion}/login`;
  const params = {
    method: "POST",
    body: JSON.stringify(data),
    headers: {
      "Content-type": "application/json",
    },
  };

  fetch(url, params)
    .then((response) => {
      return response.json();
    })
    .then((result) => {
      if (result.user) {
        return result;
      }
      return false;
    });
}
