import React from 'react';
import {Link} from "react-router-dom"
import {List,Button,Modal,notification} from "antd";
import {EyeOutlined,EditOutlined,DeleteOutlined} from "@ant-design/icons";
import  {getAccessTokenApi} from "../../../../api/auth"
import {deletePostApi} from "../../../../api/post.js";
import "./PostsList.scss";

const {confirm} = Modal;

export default function PostsList(props) {
   const {posts,setReloadPosts} = props;
   
   const deletePost = post =>{
       const accesToken = getAccessTokenApi();
       confirm({
           title:"Eliminado post",
           content:`¿Esta seguro que desea eliminar el post ${post.title}?`,
           okText:"Eliminar",
           okType:"danger",
           cancelText:"Cancelar",
           onOk(){
               deletePostApi(accesToken,post.id).then(response =>{
                   const typeNotification = response.code ===200 ? "success": "warning";
                   notification[typeNotification]({
                       message: response.message
                   })
                   setReloadPosts(true);
               }).catch( () =>{
                   notification["error"]({
                       message:"Error del servidor, intentelo mas tarde"
                   })
               })
           }
       })

   }
    return (
        <div className="posts-list">
           <List 
             dataSource={posts.docs}
             renderItem={post => <Post post={post} deletePost={deletePost} />}
           />
        </div>
    )
}

function Post(props){
 const {post,deletePost} = props;

 return(
    <List.Item
        actions={[
            <Link to={`/blog/${post.url}`} target="_blank">
              <Button
                 type="primary"
               >   
                    <EyeOutlined/>
             </Button>
            </Link>,
            <Button type="primary">
                <EditOutlined/>
            </Button>,
             <Button type="danger" onclick={deletePost(post)}>
             <DeleteOutlined/>
         </Button>
        ]}
    >
        <List.Item.Meta 
            title={post.title}

        />
    </List.Item>
        
 
    )
}
