import React,{useState,useEffect} from 'react';
import {Row,Col,Form,Input,Button,DatePicker,notification} from "antd";
import {FontSizeOutlined,LinkOutlined} from "@ant-design/icons";
import moment from "moment";
import "moment/locale/es-mx";
import locale from 'antd/lib/locale/es-mx';
import {Editor} from "@tinymce/tinymce-react"
import {getAccessTokenApi} from "../../../../api/auth";
import "./AddEditPostForm.scss";
export default function AddEditPostForm(props) {
    const {setIsvibleModal,setReloadPosts,post} = props;
    const [postData,setPostData] = useState({});

    useEffect(() =>{
        if(post){
            setPostData(post);
        }else{
            setPostData({});
        }
    },[post])

    return (
        <div className="add-edit-post-form">
            <AddEditForm postData={postData} setPostData={setPostData} post={post}/>
        </div>
    )
}

function AddEditForm(props){
    const {postData,setPostData,post} = props;

    return(
            <Form
             className="add-edit-post-form"
             
             >
                <Row gutter={24}>
                    <Col span={8}>
                       <Form.Item>
                       <Input 
                            prefix={<FontSizeOutlined />}
                            placeholder="Titulo"
                            //value={}
                            //onChange={}
                        />
                       </Form.Item>
                    </Col>
                    <Col span={8}>
                       <Form.Item>
                       <Input 
                            prefix={<LinkOutlined />}
                            placeholder="url"
                            //value={}
                            //onChange={}
                        />
                       </Form.Item>
                    </Col>
                    <Col span={8}>
                       <Form.Item>
                       <DatePicker
                            style={{width:"100%"}}
                            format="DD/MM/YY HH:mm:ss"
                            showTime={{ defaultValue: moment('00:00:00', 'HH:mm:ss'), }}
                            placeholder="Fecha depublicaciÃ³n"
                              //value={}
                            //onChange={}
                        />
                       </Form.Item>
                    </Col>
                    
                </Row>
                
            </Form>
      
    )
}
