import React, { useState,useEffect } from 'react';
import {Form,Input,Button,notification} from "antd"
import {FontSizeOutlined,LinkOutlined} from "@ant-design/icons";
import {updateMenuApi} from "../../../../api/menu";
import {getAccessTokenApi} from "../../../../api/auth";

import "./EditMenuWebForm.scss";

export default function EditMenuWebForm(props){

    const {setIsVisibleModal,setReloadMenuWeb,menu} = props;
    const [menuWebData, setMenuWebData] = useState(menu);

    useEffect(() => {
        setMenuWebData(menu);
    },[menu])

    const editMenu =  event =>{
        console.log(menuWebData);
    }

    return (
        <div className="edit-menu-web-form">
          <EditForm
           meunWebData={menuWebData}
           setMenuWebData={setMenuWebData}
           editMenu={editMenu}

          />
        </div>
        )
}

function EditForm(props){
    const {menuWebData, setMenuWebData, editMenu} = props;

    return(
            <Form className = "form-edit" onFinish={editMenu}>
                <Form.Item>
                    <Input
                        prefix = {<FontSizeOutlined style={{color: "rgba(0,0,0,0.25)"}} />}
                        placeholder="Titulo"
                        //value={menuWebData.title}
                        onChange={e => setMenuWebData({...menuWebData, title: e.target.value})}
                    />
                </Form.Item>
                <Form.Item>
                  <Input
                        prefix = {<LinkOutlined style={{color: "rgba(0,0,0,0.25)"}} />}
                        placeholder="URL"
                        value={menuWebData.url}
                        onChange={e => setMenuWebData({...menuWebData, url: e.target.value})}
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                    type="primary"
                    htmlType="submit"
                    className="btn-submit"
                    onClick={editMenu}
                    >
                     Actualizar men√∫
                    </Button>
                </Form.Item>
            </Form>
                
        )
}
