import React,{useState,useEffect} from 'react';
import {Switch,List,Avatar,Button} from "antd";
import {EditOutlined,StopOutlined,DeleteOutlined,CheckOutlined  } from "@ant-design/icons";
import EditUserForm from "../EditUserForm"
import {getAvatarApi,activeteUserApi} from "../../../api/user"
import {getAccessTokenApi} from "../../../api/auth"
import Modal from "../../../Modal"

 import NoAvatar from "../../../assets/images/png/9.1 no-avatar.png"
 import ".//ListUsers.scss"

 export default function ListUsers(props){
     const {usersActive, usersInactive,setReloadUsers} =  props;
     const [viewUsersActive, setViewUsersActive] = useState(true)
     const [isVisibleModal, setIsVisibleModal] = useState(false)
     const [modalTitle, setModalTitle] = useState("")
     const [modalContent, setModalContent] = useState(null)
    
     return(
        
         <div className="list-users">
             <div className="list-users-switch">
                <Switch defaultChecked onChange={()=>{
                    setViewUsersActive(!viewUsersActive)
                }}
                />
                <span>
                    {viewUsersActive ? " Usuarios Activos" : " Usuarios Inactivos"}
                </span>
             </div>
            {viewUsersActive ?(
                 <UsersActive 
                 usersActive={usersActive} 
                 setIsVisibleModal={setIsVisibleModal} 
                 setModalTitle={setModalTitle}
                 setModalContent={setModalContent}
                 setReloadUsers={setReloadUsers}
                 />
                  ):(
                 <UsersInactive usersInactive={usersInactive}/>
                  ) }
            
            <Modal  title={modalTitle} isVisible={isVisibleModal} setIsVisible={setIsVisibleModal} >
                {modalContent}
            </Modal>

         </div>
     )
 }

 function UsersActive(props){
     const {usersActive,setIsVisibleModal,setModalTitle,setModalContent,setReloadUsers} = props;
     
     const editUser = user =>{
         setIsVisibleModal(true)
         setModalTitle(`Editando Usuario: ${user.name} ${user.lastName}`);
         setModalContent(<EditUserForm user={user} setIsVisibleModal={setIsVisibleModal} setReloadUsers={setReloadUsers}/>);
     }
      

   
     return (
         <List 
         className="users-active"
         itemLayout="horizontal"
         dataSource={usersActive}
         renderItem={user => <UserActive user={user} editUser={editUser}/>}
         />
     )
 }


 function UserActive(props){
  const { user, editUser} = props;
  
  const token = getAccessTokenApi;
  const activateUser = activeteUserApi
  const [avatar, setAvatar] = useState(null);


  const [activate, setActivate ]  =useState(true) 
  const id = user.id;                                            ////////////////////////////////
  
  useEffect(()=>{
     if (user.avatar){
         getAvatarApi(user.avatar).then(response =>{
             setAvatar(response)
         })
     }else{
         setAvatar(null);
     }
  },[user])

  return(
        <List.Item
                
        actions={[
            <Button
            type="primary"
                onClick={ () => editUser(user)}
                icon={<EditOutlined/>}
            />,

            <Button
                type="danger"
                onClick={()=> activateUser(token,id,!activate)}
                icon={<StopOutlined/>}
                title="Desactivar Ususario"
            />,
            <Button
            type="danger"
            onClick={()=>console.log("Elimar Usuario...")}
            icon={<DeleteOutlined/>}
            tile="Eliminar Usuario"
            />

        
            
        ]}
    
    >
        
        <List.Item.Meta
            avatar={<Avatar src={avatar ? avatar : NoAvatar}/>}
            title={`
            ${user.name ? user.name : "No existe nombre"}
            ${user.lastName ? user.lastName : "No existe Apellido"}
        
            `  
            }
            description={user.email}
            
        />
    </List.Item>
    )
    }

    function UsersInactive(props){
        const {usersInactive} = props
        return ( 
            <List 
            className="users-active"
            itemLayout="horizontal"
            dataSource={usersInactive}
            renderItem={user => <UserInactive user={user}/>}
            />
        )
}

    function UserInactive (props){
        const {user} = props;
        const [avatar, setAvatar] = useState(null);
  
        useEffect(()=>{
           if (user.avatar){
               getAvatarApi(user.avatar).then(response =>{
                   setAvatar(response)
               })
           }else{
               setAvatar(null);
           }
        },[user])
        
        return(
            <List.Item
                
            actions={[
                <Button
                    type="primary"
                    onClick={()=> console.log("Activar Usuario...")}
                    icon={<CheckOutlined/>}
                />,

                
                <Button
                type="danger"
                onClick={()=>console.log("Elimar Usuario...")}
                icon={<DeleteOutlined/>}
                />

                
                
            ]}
            
            >
            
                <List.Item.Meta
                avatar={<Avatar src={avatar ? avatar : NoAvatar}/>}
                title={`
                ${user.name ? user.name : "No existe nombre"}
                ${user.lastName ? user.lastName : "No existe Apellido"}
                
                `  
                }
                description={user.email}
                />
            </List.Item>
        )
    }
