import React, { useState } from "react";
import { Form, Input, Button, Checkbox, notification, Space } from "antd";
import { UserOutlined, LockOutlined } from "@ant-design/icons";

import "./RegisterForm.scss";

export default function RegisterForm() {
  const [input, setInput] = useState({
    email: "",
    password: "",
    repeatPassword: "",
    privatePolicy: false,
  });

  const [formValid, setFormValid] = useState({
    email: false,
    password: false,
    repeatPassword: false,
    privatePolicy: false,
  });

  const changeForm = (e) => {
    if (e.target.name === "privatePolicy") {
      setInput({
        ...input,
        [e.target.name]: e.target.checked,
      });
    } else {
      setInput({
        ...input,
        [e.target.name]: e.target.value,
      });
    }
  };

  const inputValidation = () => {};

  const register = () => {
    console.log(input);
  };
  return (
    <Form className="register-form" onFinish={register} onChange={changeForm}>
      <Form.Item>
        <Input
          type="email"
          name="email"
          placeholder="Email"
          className="register-form-input"
          prefix={<UserOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          onChange={inputValidation}
          value={input.email}
        />
      </Form.Item>
      <Form.Item>
        <Input
          type="password"
          name="password"
          placeholder="Contraseña"
          className="register-form-input"
          prefix={<LockOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          onChange={inputValidation}
          value={input.password}
        />
      </Form.Item>
      <Form.Item>
        <Input
          type="password"
          name="repeatPassword"
          placeholder="Repita su contraseña"
          className="register-form-input"
          prefix={<LockOutlined style={{ color: "rgba(168, 171, 174 )" }} />}
          onChange={inputValidation}
          value={input.repeatPassword}
        />
      </Form.Item>
      <Form.Item>
        <Checkbox
          name="privatePolicy"
          checked={input.privatePolicy}
          onChange={inputValidation}
        >
          He leido y acepto la politica de privacidad.
        </Checkbox>
      </Form.Item>
      <Form.Item>
        <Space direction="horizontal" size={20}>
          <Button htmlType="reset" className="register-form-button">
            Borrar
          </Button>
          <Button htmlType="submit" className="register-form-button">
            Crear cuenta
          </Button>
        </Space>
      </Form.Item>
    </Form>
  );
}
