import {basePath,apiVersion} from "./config";

export function getPostsApi(page,limit){
    const url = `${basePath}/${apiVersion}/get-posts?page=${page}&limit=${limit}`

   return fetch(url).then(response =>{
        return response.json();

    }).then(result =>{
        return result
    }).catch(err =>{
        return err
    })
}
