import React,{useState,useEffect} from 'react';
import {Form,Button,Input,notification} from "antd";
import {KeyOutlined,GiftOutlined,DollarOutlined,LinkOutlined} from "@ant-design/icons";
import {addCourseApi } from "../../../../api/course"

import "./AddEditCourseForm.scss";

export default function AddEditCourseForm(props) {
    const {setIsVisibleModal, setReloadCourses, courses} = props;
    const [courseData, setCourseData] = useState({});

    const addCourse = () =>{
        console.log("creando curso function")
    }

    const updateCourse = () =>{
        console.log("actualizando curso.")
    }

    return (
        <div className="add-Edit-course-form">
            <AddEditForm
                courses={courses}
                addCourse={addCourse}
            />
        </div>
    )
}

function AddEditForm(props){
    const {courses,addCourse} = props;
    return(
            <Form 
            className="form-add-edit"
            onFinish={() => addCourse()}
            >
                <Form.Item>
                    <Input 
                        prefix={<KeyOutlined/>}
                        placeholder="ID del curso"
                        // value={}
                        // onChange={}
                        disabled={courses ?true : false}
                    />
                </Form.Item>
                <Form.Item>
                    <Input 
                        prefix={<LinkOutlined/>}
                        placeholder="URL del curso"
                        // value={}
                        // onChange={}
                    />
                </Form.Item>
                <Form.Item>
                    <Input 
                        prefix={<GiftOutlined/>}
                        placeholder="Cupon de descuento"
                        // value={}
                        // onChange={}
                    />
                </Form.Item>
                <Form.Item>
                    <Input 
                        prefix={<DollarOutlined/>}
                        placeholder="Precio del curso"
                        // value={}
                        // onChange={}
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                     type="primary"
                     htmlType="submit"
                     className="btn-submit"
                    >
                        {courses ? "Actualizar curso" : "Crear curso"}
                    </Button>
                </Form.Item>
            </Form>
        )

}
